import { useState, useEffect} from "react";
import './css/App.css';
import InputMask from 'react-input-mask';

function PhoneInput(props) {
  return (
    <InputMask 
      mask='(+1) 999 999 9999' 
      value={props.value} 
      onChange={props.onChange}>
    </InputMask>
  );
}


function App() {
  const initialValues = { username: "", email: "", phone: "", date: "", message: ""};
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [phone, setPhone] = useState('');
  const handleInput = ({ target: { value } }) => setPhone(value);


  const handleChange = (e) => {
    console.log(e.target);
    const { name, value } = e.target;
    setFormValues({...formValues, [name]: value })
    console.log(formValues);
  };

  useEffect(() => {
    console.log(formErrors); 
    if(Object.keys(formErrors).length === 0 && isSubmit){
      console.log(formValues);
    }
  },[formErrors])

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;



   if (!values.username) {
    errors.username = "Username is required!";
   }
   if (!values.email) {
    errors.email = "Email is required!";
   } else if (!regex.test(values.email)){
    errors.email = "This is not a valid email format!";
   }
   if (!values.date) {
    errors.date = "Birthday is required!";
   }
   if (!values.message) {
    errors.message = "Message is required!";
   } else if (values.message.length < 10 || values.message.length > 300) {
    errors.message = "Минимальная длина- 10 символов, максимальная- 300"
   }

   return errors; 
  };




  return (
    <div className='container'>
      {Object.keys(formErrors).length === 0 && isSubmit ? (<div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
       )}
      <form onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <div clasName="ui divider"></div>
        <div className="ui form"></div>
        <div className="field">
        <label>Имя Фамилия</label>
        <input 
        type="text" 
        name="username" 
        plceholder="Username" 
        value={formValues.username}
        onChange={handleChange}
        />
        <p>{formErrors.username}</p>
        </div>


        <div className="field">
        <label>E-mail</label>
        <input 
        type="email" 
        name="email" 
        plceholder="Email" 
        value={formValues.email}
        onChange={handleChange}
        />
        <p>{formErrors.email}</p>
        </div>

        <div className="field">
        <label>Номер телефона</label>
        <PhoneInput 
        value={phone} 
        onChange={handleInput}>
      </PhoneInput>
      </div>


        <div className="field">
        <label>Дата рождения</label>
        <input 
        type="date" 
        name="date" 
        plceholder="date" 
        value={formValues.date}
        onChange={handleChange}
        />
        <p>{formErrors.date}</p>
        </div>
        <div className="field">
        <label>Сообщение</label>
        <textarea 
        type="text" 
        name="message" 
        plceholder="message" 
        value={formValues.message}
        onChange={handleChange}
        />
        <p>{formErrors.message}</p>
        </div>

        <button className="fluid ui button blue">Submit</button>
      </form>
    </div>
  );
}



export default App;
