import { useState, useEffect, useRef} from "react";
import './css/App.css';

function App() {
  const initialValues = { username: "", email: "", phone: "", date: "", message: ""};
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [tel, setTel] = useState(['+', '7', ' ', '(']);
  const [telErr, setTelErr] = useState(false);
  const [telMess, setTelMess] = useState('');

  const inputPhone = useRef();

  const handleChange = (e) => {
    console.log(e.target);
    const { name, value } = e.target;
    setFormValues({...formValues, [name]: value });
  };

  const maskAdd = (e) => {
    if (tel.length === 0) {
      setTel(['+', '7', ' ', '('])
      e.currentTarget.classList.remove(s.error)
      setTelErr(false)
      setTelMess('')
    }
  }

  const handleChangeTel = (e) => {
    if (e.target.nextSibling.classList.contains(s.messErrorReq)) {
      e.target.nextSibling.remove()
      e.currentTarget.classList.remove(s.error)
    }

    let regTel = /[^0-9 \-)(+]/

    if (e.target.value.length < tel.length) {
      tel.pop()
      setTel([...tel])
    }
    else if (e.target.value.length === 3 && tel[0] === '+') {
      setTel([...tel, ' ', '(', e.target.value.slice(-1)])
    }
    else if (e.target.value.length === 4 && tel[0] === '+') {
      setTel([...tel, '(', e.target.value.slice(-1)])
    }
    else if (e.target.value.length === 7 && tel[0] === '+') {
      setTel([...tel, e.target.value.slice(-1), ')', ' '])
    }
    else if (e.target.value.length === 8 && tel[0] === '+') {
      setTel([...tel, ')', ' ', e.target.value.slice(-1)])
    }
    else if (e.target.value.length === 12) {
      setTel([...tel, e.target.value.slice(-1), '-'])
    }
    else if (e.target.value.length === 13 && tel[0] === '+') {
      setTel([...tel, '-', e.target.value.slice(-1)])
    }
    else if (e.target.value.length === 15) {
      setTel([...tel, e.target.value.slice(-1), '-'])
    }
    else if (e.target.value.length === 16 && tel[0] === '+') {
      setTel([...tel, '-', e.target.value.slice(-1)])
    }
    else if (tel[0] !== '+' && e.target.value.length === 10) {
      setTelErr(false)
      setTelMess('')
      e.currentTarget.classList.remove(s.error)
      setTel(['+', '7', ' ', '(', e.target.value.charAt(0), e.target.value.charAt(1), e.target.value.charAt(2),
        ')', ' ', e.target.value.charAt(3), e.target.value.charAt(4), e.target.value.charAt(5), '-',
        e.target.value.charAt(6), e.target.value.charAt(7), '-', e.target.value.charAt(8), e.target.value.charAt(9)])
    }
    else if (tel[0] === '+' && e.target.value.length === 18) {
      setTelErr(false)
      setTelMess('')
      setTel([...tel, e.target.value.slice(-1)])
      e.currentTarget.classList.remove(s.error)
    }

    else if (!regTel.test(e.target.value)) {
      setTel([...tel, e.target.value.slice(-1)])
    }

    e.target.value = e.target.value.replace(regTel, '')

    if (tel[0] !== '+' && e.target.value.length < 10) {
      e.currentTarget.classList.add(s.error)
      setTelErr(true)
      setTelMess('Минимум 10 цифр!')
    }
  }
  const offFocusTel = (e) => {
    if (tel[0] === '+' && e.target.value.length < 18) {
      e.currentTarget.classList.add(s.error)
      setTelErr(true)
      setTelMess('Некоректный номер!')
    }
  }






  useEffect(() => {
    console.log(formErrors); 
    if(Object.keys(formErrors).length === 0 && isSubmit){
      console.log(formValues);
    }
  },[formErrors])

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;


   if (!values.username) {
    errors.username = "Username is required!";
   }
   if (!values.email) {
    errors.email = "Email is required!";
   } else if (!regex.test(values.email)){
    errors.email = "This is not a valid email format!";
   }
   if (!values.date) {
    errors.date = "Birthday is required!";
   }
   if (!values.message) {
    errors.message = "Message is required!";
   } else if (values.message.length < 10 || values.message.length > 300) {
    errors.message = "Минимальная длина- 10 символов, максимальная- 300"
   }

   return errors; 
  };


  return (
    <div className='container'>
      {Object.keys(formErrors).length === 0 && isSubmit ? (<div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
       )}
      <form onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <div clasName="ui divider"></div>
        <div className="ui form"></div>

        <div className="field">
        <label>Имя Фамилия</label>
        <input 
        type="text" 
        name="username" 
        plceholder="Username" 
        value={formValues.username}
        onChange={handleChange}
        />
        <p>{formErrors.username}</p>
        </div>


        <div className="field">
        <label>E-mail</label>
        <input 
        type="email" 
        name="email" 
        plceholder="Email" 
        value={formValues.email}
        onChange={handleChange}
        />
        <p>{formErrors.email}</p>
        </div>

        <div className="field">
        <label>Номер телефона</label>
        <input 
        name="tel"
        maxLength={18}
        value={tel.join('')}
        autoComplete='off'
        type={'tel'}
        onChange={handleChangeTel}
        onClick={maskAdd}
        onBlur={offFocusTel}
      />
      </div>


        <div className="field">
        <label>Дата рождения</label>
        <input 
        type="date" 
        name="date" 
        plceholder="date" 
        value={formValues.date}
        onChange={handleChange}
        />
        <p>{formErrors.date}</p>
        </div>

        <div className="field">
        <label>Сообщение</label>
        <textarea 
        type="text" 
        name="message" 
        plceholder="message" 
        value={formValues.message}
        onChange={handleChange}
        />
        <p>{formErrors.message}</p>
        </div>

        <button className="fluid ui button blue">Submit</button>
      </form>
    </div>
  );
}


export default App;
